package com.mirth.connect.simplesender;

import java.io.File;
import java.io.FileNotFoundException;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;
import java.util.UUID;

import org.apache.log4j.Level;
import org.apache.log4j.Logger;

public class SimpleSender {
    private static int count = 0;
    private static int iteration = 0;
//	private Logger logger = Logger.getLogger(this.getClass().getName());
    static final Logger logger = Logger.getLogger("SimpleSender");
	
    public static void main(String[] args) {
        if (args.length < 3) {
            System.out.println("Correct usage:  java SimpleSender <MessageFile.txt> <IP> <port> [-i | -s <iterations_or_seconds>] \n" + "             or java SimpleSender postgres <IP> <port> <schema> <username> <password>");
            return;
        }

        final boolean tcp;
        if (args[0].equalsIgnoreCase("POSTGRES")) {
            if (args.length != 6) {
                System.out.println("Correct usage:  java SimpleSender <MessageFile.txt> <IP> <port> [iterations]\n" + "             or java SimpleSender postgres <IP> <port> <schema> <username> <password>");
                return;
            }
            tcp = false;
        } else
            tcp = true;

        class Timer implements Runnable {
        	//ogni dieci secondi fa un log del numero di messaggi inviati e del numero di volte che ha inviato il messaggio
            public void run() {
                try {
                    while (true) {
                        Thread.sleep(10000);	//10 sec
                        if (tcp) {
                        	logger.log(Level.INFO, count + " total messages sent.  " + iteration + " iterations complete.");
//                            System.out.println(count + " total messages sent.  " + iteration + " iterations complete.");
                        } else {
//                            System.out.println(count / 10 + " messages/second.");
                            logger.log(Level.INFO, count / 10 + " messages/second.");
                            count = 0;
                        }
                    }
                } catch (InterruptedException e) {
                    // should happen when closed.
                }
            }
        }

        Timer t = new Timer();
        Thread thread = new Thread(t);
        thread.start();

        if (tcp)
            new SimpleSender().runTCPSender(args);
        else
            new SimpleSender().runPostgresSender(args);
    }

    /**
     * invia i messaggi letti da un file verso un server tcp con un server in ascolto con i protocollo hl7
     * 
     * @param args
     */
    private void runTCPSender(String[] args) {
        TCPSender tcpSender = new TCPSender();

        ArrayList<String> hl7messages = getHl7messages(new File(args[0]));

        String IP = args[1];
        String port = args[2];

        int iterations = 0;
        int seconds = 0;
        if (args.length > 4) {
            String iterationsOrTime = args[3];
            if (iterationsOrTime.equalsIgnoreCase("-s"))
                seconds = Integer.parseInt(args[4]);
            else if (iterationsOrTime.equalsIgnoreCase("-i"))
                iterations = Integer.parseInt(args[4]);
        }

        long startTime = System.currentTimeMillis();
        while ((iterations == 0 || iteration < iterations) && (seconds == 0 || (System.currentTimeMillis() - startTime) < (seconds * 1000))) {
        	
            for (int j = 0; j < hl7messages.size(); j++) {
                if (seconds != 0) {
                    if ((System.currentTimeMillis() - startTime) >= (seconds * 1000))	//time out
                        break;
                }
                
                List<String> answer = null;
                boolean sent = false;
                while (!sent) {
                    try {
                        sent = true;
                        answer = tcpSender.send(hl7messages.get(j), IP, port);
                    } catch (Exception e) {
                        sent = false;
                    }
                }
                count++;
                
//                System.out.println("risposta del server:");
                logger.log(Level.INFO, "risposta del server:");
                for (String r : answer) {
	               	 logger.log(Level.INFO, r);
//                	System.out.println(r);
				}
               
                
            }
            iteration++;
        }
        long endTime = System.currentTimeMillis();
        logger.log(Level.INFO, "Total time to send " + count + " messages: " + ((endTime - startTime) / 1000) + " seconds.");
        System.exit(0);
    }
    
    /**
     * 
     * @param file
     * @return Hl7messages ogni elemento dell'array contiene un messaggio completo
     */
    private ArrayList<String> getHl7messages(File file) {
        ArrayList<String> hl7messages = new ArrayList<String>();
        Scanner s = null;
        try {
            s = new Scanner(file);
            String message = "";

            if (s.hasNext())
                message = s.nextLine();
            while (s.hasNext()) {
                String temp = s.nextLine();
                if (temp.length() == 0) {
                    hl7messages.add(message);
                    message = "";
                    temp = s.nextLine();
                    if (s.hasNext())
                        message = s.nextLine();
                } else {
                    message += "\r" + temp;
                }
            }
            if (message.length() > 0)
                hl7messages.add(message);
        } catch (FileNotFoundException e1) {
            e1.printStackTrace();
        } finally {
        	if (s != null) s.close();
        }
		return hl7messages;
    }

    private void runPostgresSender(String[] args) {
        PostgresSender postgresSender = new PostgresSender(args[1], args[2], args[3], args[4], args[5]);
        String rawData = "";
        String transformedData = "";
        String encodedData = "";
        String channelId = null;

        ResultSet result = postgresSender.runQuery("SELECT ID FROM CHANNEL LIMIT 1");
        try {
            result.next();
            channelId = result.getString(1);
        } catch (SQLException e) {
            e.printStackTrace();
        } finally {
            if (result != null) {
                try {
                    result.close();
                } catch (SQLException e) {
                    e.printStackTrace();
                }
            }
        }

        if (channelId == null) {
            System.out.println("ERROR: The database must have a channel in it.");
            return;
        }

        final String channelIdFinal = channelId;
        final PostgresSender postgresSenderFinal = postgresSender;
        
        class Updater implements Runnable {
            public void run() {
                try {
                    while (true) {
                        Thread.sleep(1000);
                        postgresSenderFinal.runUpdate("UPDATE CHANNEL_STATISTICS SET RECEIVED = 0, FILTERED = 0, SENT = 0, ERROR = 0, QUEUED = 0 WHERE SERVER_ID = '82fdde27-a896-453b-858f-a4746864014d' AND CHANNEL_ID='" + channelIdFinal + "'");
                    }
                } catch (InterruptedException e) {
                    // should happen when closed.
                }
            }
        }

        Updater u = new Updater();
        Thread thread = new Thread(u);
        thread.start();

        while (true) {
            boolean worked = true;

            worked = worked && postgresSender.runUpdate("INSERT INTO MESSAGE (ID, SERVER_ID, CHANNEL_ID, SOURCE, TYPE, DATE_CREATED, VERSION, IS_ENCRYPTED, STATUS, RAW_DATA, RAW_DATA_PROTOCOL, TRANSFORMED_DATA, TRANSFORMED_DATA_PROTOCOL, ENCODED_DATA, ENCODED_DATA_PROTOCOL, CONNECTOR_NAME, ERRORS, CORRELATION_ID, CONNECTOR_MAP, CHANNEL_MAP, RESPONSE_MAP) VALUES " + "('" + UUID.randomUUID().toString() + "', '82fdde27-a896-453b-858f-a4746864014d', '" + channelId + "', '1', 'ADT-A04', '2007-05-10 20:35:35.384', '2.3.1', 'FALSE', 'TRANSFORMED', '" + rawData + "', 'HL7V2', '" + transformedData + "', 'XML', '" + encodedData + "', 'HL7V2', NULL, NULL, NULL, '<map/>', '<map/>', '<map/>')");
            worked = worked && postgresSender.runUpdate("INSERT INTO MESSAGE (ID, SERVER_ID, CHANNEL_ID, SOURCE, TYPE, DATE_CREATED, VERSION, IS_ENCRYPTED, STATUS, RAW_DATA, RAW_DATA_PROTOCOL, TRANSFORMED_DATA, TRANSFORMED_DATA_PROTOCOL, ENCODED_DATA, ENCODED_DATA_PROTOCOL, CONNECTOR_NAME, ERRORS, CORRELATION_ID, CONNECTOR_MAP, CHANNEL_MAP, RESPONSE_MAP) VALUES " + "('" + UUID.randomUUID().toString() + "', '82fdde27-a896-453b-858f-a4746864014d', '" + channelId + "', '1', 'ADT-A04', '2007-05-10 20:35:35.384', '2.3.1', 'FALSE', 'TRANSFORMED', '" + rawData + "', 'HL7V2', '" + transformedData + "', 'XML', '" + encodedData + "', 'HL7V2', NULL, NULL, NULL, '<map/>', '<map/>', '<map/>')");

            if (worked)
                count++;
            else
                System.out.println("Query failed.");
        }
    }
}
